import { createReducer, on } from '@ngrx/store';
import * as OrdersActions from './orders.actions';
export const initialState = {
  orders: [],
  loading: false,
  error: null
};
export const ordersReducer = createReducer(initialState, on(OrdersActions.loadOrders, state => ({
  ...state,
  loading: true,
  error: null
})), on(OrdersActions.loadOrdersSuccess, (state, {
  orders
}) => ({
  ...state,
  orders,
  loading: false
})), on(OrdersActions.loadOrdersFailure, (state, {
  error
}) => ({
  ...state,
  loading: false,
  error
})));
